using Cosmos.System.Graphics;
using Cosmos.System.Graphics.Fonts;
using CosmosTTF;
using System;
using System.Collections.Generic;
using System.Drawing;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using Venera.Shell;

namespace Venera.Graphics
{
    /// <summary>
    /// The Chromat is a fullscreen graphical terminal. It manages various things like cursor
    /// position, font, color and many more things.
    /// </summary>
    public class Chromat
    {
        private static readonly string ttfFont = "";
        private TTFFont noto;

        private byte[] textBuffer;
        private uint cursorPosition;

        public uint Width { get; private set; }
        public uint Height { get; private set; }
        public uint StatusBarHeight { get; private set; }
        public int FontSize { get; private set; }
        private HashSet<BuiltIn> foo = new();

        public int CursorY
        {
            get
            {
                return (int)(cursorPosition % Height);
            }
        }

        public int CursorX
        {
            get
            {
                return (int)(cursorPosition / Width);
            }
        }

        private Canvas canvas;
        private CGSSurface surface;

        public Chromat(uint width = 800, uint height = 600, uint statusBarHeight = 8, int fontSize = 16)
        {
            Width = width;
            Height = height;
            StatusBarHeight = statusBarHeight;
            FontSize = fontSize;

            textBuffer = new byte[Width * Height];
            canvas = FullScreenCanvas.GetFullScreenCanvas(new Mode(Width, Height, ColorDepth.ColorDepth32));
            surface = new CGSSurface(canvas);

            noto = new TTFFont(Convert.FromBase64String(ttfFont));
            StatusBarHeight = statusBarHeight;
        }

        public void Display()
        {
            canvas.Clear(Color.Blue);
            canvas.Display();
        }

        public void DrawText(string text)
        {
            foreach (char c in text)
            {
            }

            noto.DrawToSurface(surface, FontSize, CursorX, CursorY, text, Color.White);
            canvas.Display();
        }
    }
}
